mapper.select:
SELECT id,
       parent_id AS parentId
FROM api_debug_module
WHERE protocol = #{protocol}

  AND create_user = #{userId}

SELECT id
FROM api_debug_module
WHERE project_id = #{0}

SELECT max(pos)
FROM api_debug_module
WHERE parent_id = #{0}

SELECT *
from api_debug_module
WHERE parent_id = #{parentId}
  AND pos > #{pos}
order by pos asc
limit 1

SELECT id, NAME, module_id AS parentId, 'api' AS type, method FROM api_debug WHERE protocol = #{protocol}
AND create_user = #{userId}
ORDER BY update_time DESC

sql.where:
f.create_user = #{userId}

    AND f.name like CONCAT('%', #{request.keyword},'%')


    AND f.module_id IN

        #{item}



    AND f.protocol = #{request.protocol}
