mapper.select:
select t1.api_definition_id apiDefinitionId, count( t1.id ) caseTotal, SUM( CASE WHEN t2.`status` = 'SUCCESS' THEN 1 ELSE 0 END ) AS `success`, SUM( CASE WHEN t2.`status` = 'ERROR' THEN 1 ELSE 0 END ) AS `error`, SUM( CASE WHEN t2.`status` = 'FAKE_ERROR' THEN 1 ELSE 0 END ) AS `fakeError`, CONCAT( FORMAT( SUM( IF ( t2.`status` = 'SUCCESS', 1, 0 ))/ COUNT( t1.id )* 100, 2 ), '%' ) casePassRate FROM api_test_case t1 LEFT JOIN api_report t2 ON t1.id = t2.resource_id WHERE t1.project_id = #{projectId} and t1.deleted = 0
GROUP BY t1.api_definition_id HAVING t1.api_definition_id IN
#{value}

SELECT pos
FROM api_definition
WHERE project_id = #{projectId}
ORDER BY pos DESC
LIMIT 1;

