mapper.update:
update api_test_case set last_report_status = '-' where id in (select api_test_case_id from api_test_case_record where api_report_id in
#{id}
)

mapper.select:
select ar.* from api_report ar where ar.deleted = false and ar.test_plan_case_id = 'NONE'
and ( ar.name like concat('%', #{request.keyword},'%') )
and ar.project_id = #{request.projectId}

select ar.id from api_report ar where ar.deleted = false and ar.test_plan_case_id = 'NONE'
and ( ar.name like concat('%', #{request.condition.keyword},'%') )
and ar.project_id = #{request.projectId}

select distinct ar.* , project.organization_id , ar.test_plan_case_id as testPlanId, if (ar.test_plan_case_id = 'NONE' and ar.integrated =0, a.api_test_case_id , ar.test_plan_case_id) as resourceId from api_report ar LEFT JOIN api_test_case_record a ON ar.id = a.api_report_id LEFT JOIN api_test_case c ON a.api_test_case_id = c.id left join project on ar.project_id = project.id where ar.deleted = false and ar.plan = 0 and ar.start_time BETWEEN #{startTime} AND #{endTime} and ar.exec_status in ('PENDING', 'RUNNING', 'RERUNNING')
and ar.id in
#{id}
and ar.project_id in
#{projectId}
and (
if(ar.integrated, ar.id, c.num) like concat('%', #{request.condition.keyword},'%')
or if(ar.integrated, ar.name,c.name) like concat('%', #{request.condition.keyword},'%')
)

select id , name from api_report
where id in
#{id}

select distinct ar.* from api_report ar INNER JOIN api_report_step ars on ar.id = ars.report_id

    where ar.id in

        #{id}
