mapper.update:
update api_scenario
set last_report_status = '-'
where id in (
select api_scenario_id from api_scenario_record where api_scenario_report_id in

    #{id}

    )

mapper.select:
select asr.* from api_scenario_report asr where asr.deleted = false and asr.test_plan_scenario_id = 'NONE'
and ( asr.name like concat('%', #{request.keyword},'%') )
and asr.project_id = #{request.projectId}

select asr.id from api_scenario_report asr where asr.deleted = false and asr.test_plan_scenario_id = 'NONE'
and ( asr.name like concat('%', #{request.condition.keyword},'%') )
and asr.project_id = #{request.projectId}

select distinct asr.*, project.organization_id, asr.test_plan_scenario_id as testPlanId, if (asr.test_plan_scenario_id = 'NONE' and asr.integrated =0 , a.api_scenario_id , asr.test_plan_scenario_id) as resourceId from api_scenario_report asr left join api_scenario_record a on asr.id = a.api_scenario_report_id left JOIN api_scenario s on a.api_scenario_id = s.id left join project on asr.project_id = project.id where asr.deleted = false and asr.plan = 0 and asr.start_time BETWEEN #{startTime} AND #{endTime} and asr.exec_status in ('PENDING', 'RUNNING', 'RERUNNING')
and asr.id in
#{id}
and asr.project_id in
#{projectId}
and ( if(asr.integrated, asr.id, s.num) like concat('%', #{request.condition.keyword},'%') or if(asr.integrated, asr.name,s.name) like concat('%', #{request.condition.keyword},'%')
)

select distinct asr.id, asr.project_id, asr.integrated, asr.status, asr.exec_status, asr.start_time as operationTime, asr.create_user as operationName, asr.trigger_mode, asr.script_identifier, asr.start_time, asr.plan, project.organization_id,
if(asr.integrated, asr.id, s.num) AS resourceNum, if(asr.integrated, asr.name,s.name) AS resourceName, if (asr.integrated,asr.id,s.id ) AS resourceId,
t.name as poolName from api_scenario_report asr left join api_scenario_record a on asr.id = a.api_scenario_report_id INNER JOIN api_scenario s on a.api_scenario_id = s.id left JOIN test_resource_pool t on asr.pool_id = t.id left join project on asr.project_id = project.id where asr.start_time BETWEEN #{startTime} AND #{endTime} and asr.plan = 0
and asr.project_id IN
#{projectId}
and ( if(asr.integrated, asr.id, s.num) like concat('%', #{request.keyword},'%') or if(asr.integrated, asr.name,s.name) like concat('%', #{request.keyword},'%') )

select p.id as projectId, o.id as organizationId from project p inner join organization o on p.organization_id = o.id
where p.id in
#{id}

select id ,name from api_scenario_report

    where id in

        #{id}

select distinct asr.* from api_scenario_report asr inner join api_scenario_report_step ars on asr.id = ars.report_id
where asr.id in
#{id}

select asr.id, test_plan.num as testPlanNum from api_scenario_report asr inner join test_plan_api_scenario tpas on asr.test_plan_scenario_id = tpas.id inner join test_plan on tpas.test_plan_id = test_plan.id
where asr.id in
#{id}
