mapper.update:
update api_scenario_report
set status = 'STOPPED',
    update_time = #{time},
    update_user = #{userId}
where id in

    #{id}

update api_scenario
set last_report_status = 'STOPPED'
where id in (
select api_scenario_id from api_scenario_record where api_scenario_report_id in

    #{id}

    )

mapper.select:
select api_scenario_report.* from api_scenario_report where api_scenario_report.deleted = false and api_scenario_report.test_plan_id = 'NONE'
and ( api_scenario_report.name like concat('%', #{request.keyword},'%') )
and api_scenario_report.project_id = #{request.projectId}

select api_scenario_report.id from api_scenario_report where api_scenario_report.deleted = false and api_scenario_report.test_plan_id = 'NONE'
and ( api_scenario_report.name like concat('%', #{request.condition.keyword},'%') )
and api_scenario_report.project_id = #{request.projectId}

select
api_scenario_report.id
from api_scenario_report inner join api_scenario_report_step on
api_scenario_report.id = api_scenario_report_step.report_id
where api_scenario_report.start_time >= #{startTime}
and api_scenario_report.start_time <= #{endTime}

select id, pool_id from api_scenario_report where
api_scenario_report.deleted = false
and api_scenario_report.test_plan_id = 'NONE'
and api_scenario_report.start_time BETWEEN #{startTime} AND #{endTime}
and status in ('PENDING', 'RUNNING', 'RERUNNING')

    and id in

        #{id}



    and project_id in

        #{projectId}



    and (
    name like concat('%', #{request.condition.keyword},'%')
    )
