mapper.select:
select api_scenario_report.id, api_scenario_report.name, api_scenario_report.integrated, api_scenario_report.status, api_scenario_report.start_time, api_scenario_report.update_time, api_scenario_report.create_user, api_scenario_report.update_user, api_scenario_report.trigger_mode from api_scenario_report where api_scenario_report.deleted = false and api_scenario_report.test_plan_id = 'NONE'
and ( api_scenario_report.name like concat('%', #{request.keyword},'%') )
and api_scenario_report.project_id = #{request.projectId}

select api_scenario_report.id from api_scenario_report where api_scenario_report.deleted = false and api_scenario_report.test_plan_id = 'NONE'
and ( api_scenario_report.name like concat('%', #{request.condition.keyword},'%') )
and api_scenario_report.project_id = #{request.projectId}
