mapper.select:
select count(distinct case_id) as relationCaseCount, bug_id as bugId from bug_relation_case brc where brc.bug_id in
#{id}
group by brc.bug_id

select * from (
select brc.id relateId, brc.create_time, brc.case_id relateCaseId, fc.num relateCaseNum, fc.name relateCaseName, fc.project_id projectId, fc.version_id versionId, brc.case_type relateCaseType, max(brc.test_plan_id) is not null relatePlanCase from bug_relation_case brc join functional_case fc on (brc.case_id = fc.id and fc.deleted = false) where brc.bug_id = #{request.bugId}
and ( fc.name like concat('%', #{request.keyword}, '%') or fc.num like concat('%', #{request.keyword}, '%') )
group by brc.case_id union all
select brc.id relateId, brc.create_time, brc.case_id relateCaseId, atc.num relateCaseNum, atc.name relateCaseName, atc.project_id projectId, atc.version_id versionId, brc.case_type relateCaseType, max(brc.test_plan_id) is not null relatePlanCase from bug_relation_case brc join api_test_case atc on (brc.case_id = atc.id and atc.deleted = false) where brc.bug_id = #{request.bugId}
and ( atc.name like concat('%', #{request.keyword}, '%') or atc.num like concat('%', #{request.keyword}, '%') )
group by brc.case_id union all
select brc.id relateId, brc.create_time, brc.case_id relateCaseId, ao.num relateCaseNum, ao.name relateCaseName, ao.project_id projectId, ao.version_id versionId, brc.case_type relateCaseType, max(brc.test_plan_id) is not null relatePlanCase from bug_relation_case brc join api_scenario ao on (brc.case_id = ao.id and ao.deleted = false) where brc.bug_id = #{request.bugId}
and ( ao.name like concat('%', #{request.keyword}, '%') or ao.num like concat('%', #{request.keyword}, '%') )
group by brc.case_id ) tmp order by tmp.create_time desc

