mapper.select:
SELECT bug.id as bugId, bug.num as num, bug.status as status, bug.title as title, bug_relation_case.case_type as caseType, bug_relation_case.case_id as caseId, bug_relation_case.id FROM bug_relation_case INNER JOIN bug ON bug.id = bug_relation_case.bug_id WHERE bug_relation_case.test_plan_case_id IN
#{id}
and bug_relation_case.test_plan_id = #{testPlanId} ORDER BY bug.pos DESC

SELECT brc.id as id, brc.bug_id bugId, b.num as num, b.title as name, b.handle_user handleUser, b.create_user createUser, createUser.name AS createUserName, b.`status`, bc.description as content, brc.test_plan_id testPlanId, tp.name testPlanName FROM bug_relation_case brc INNER JOIN bug b ON brc.bug_id = b.id INNER JOIN bug_content bc ON brc.bug_id = bc.bug_id left join test_plan tp on brc.test_plan_id = tp.id LEFT JOIN user createUser ON b.create_user = createUser.id
and brc.test_plan_case_id = #{request.testPlanCaseId}
and b.title like concat('%', #{request.keyword},'%')
order by
brc.${sort}
brc.create_time desc
