mapper.update:
UPDATE functional_case
SET module_id = #{moduleId}
WHERE
    ref_id = #{refId}

mapper.select:
SELECT functional_case.id, functional_case.num, functional_case.NAME, functional_case.module_id, functional_case.version_id, project_version.name as versionName, functional_case.case_edit_type, functional_case.create_user, functional_case.create_time, functional_case.update_user, functional_case.update_time, functional_case.review_status, functional_case.last_execute_result, functional_case.delete_user, functional_case.delete_time, functional_case.tags, functional_case.project_id FROM functional_case LEFT JOIN project_version ON functional_case.version_id = project_version.id where functional_case.deleted = #{deleted} and functional_case.project_id = #{request.projectId}
AND functional_case.id not in ( select case_review_functional_case.case_id from case_review_functional_case where case_review_functional_case.review_id = #{request.reviewId} )
AND functional_case.id not in ( select test_plan_functional_case.functional_case_id from test_plan_functional_case where test_plan_functional_case.test_plan_id = #{request.testPlanId} )
AND functional_case.id not in
#{excludeId}

choose.when:
and functional_case.id in (
select field_id from functional_case_custom_field where concat('custom_single-',field_id) =
#{key}
and trim(both '"' from `value`) in

)

and functional_case.id in (
select case_id from functional_case_custom_field where `value` in

)

functional_case.id not in (
    select file_association.source_id from file_association where file_association.source_type = 'FUNCTIONAL_CASE'
)

mapper.select:
SELECT ref_id FROM functional_case where id in
#{id}
and deleted = #{deleted} group by ref_id

mapper.update:
update functional_case set deleted = 1, delete_user = #{userId}, delete_time = UNIX_TIMESTAMP()*1000 where ref_id in
#{id}
and deleted = false

mapper.select:
SELECT id, name, project_id from functional_case where id in
#{id}
and deleted = #{deleted}

mapper.update:
update functional_case set module_id = #{request.moduleId}, update_user = #{userId}, update_time = UNIX_TIMESTAMP()*1000 where ref_id in
#{id}
and deleted = false and project_id = #{request.projectId}

mapper.select:
select id, tags from functional_case where id in
#{id}
and deleted = false

SELECT module_id AS moduleId, count(id) AS dataCount
FROM functional_case
WHERE deleted = #{deleted}
AND project_id = #{request.projectId}


    AND functional_case.id not in (
    select case_id from case_review_functional_case where review_id = #{request.reviewId}
    )


    AND functional_case.id not in (
    select test_plan_functional_case.functional_case_id from test_plan_functional_case where test_plan_functional_case.test_plan_id = #{request.testPlanId}
    )


    AND functional_case.id not in

        #{excludeId}


GROUP BY module_id

SELECT count(id)
FROM functional_case
WHERE deleted = #{deleted}
AND project_id = #{request.projectId}


    AND functional_case.id not in (
    select case_id from case_review_functional_case where review_id = #{request.reviewId}
    )


    AND functional_case.id not in

        #{excludeId}

select `pos` from functional_case where project_id = #{projectId}

    and `pos` < #{basePos}

order by `pos` desc limit 1;

select `pos` from functional_case where project_id = #{projectId}

    and `pos` > #{basePos}

order by `pos` desc limit 1;

select fc.id
from functional_case fc
where fc.deleted = #{deleted}
    and fc.project_id = #{request.projectId}
    and fc.id not in
    (
        select brc.case_id from bug_relation_case brc where brc.bug_id = #{request.sourceId} and brc.case_type = #{request.sourceType}
    )

sql.if:
and (
fc.num like concat('%', #{request.condition.keyword}, '%') or fc.name like concat('%', #{request.condition.keyword}, '%')
)

mapper.select:
SELECT crfc.id as id,
IFNULL(history.`status`, 'UN_REVIEWED') as reviewStatus,
IFNULL(history.`status`, crfc.status) as reviewStatus,
fc.module_id as moduleId, fc.name as name, fc.id as caseId, fc.project_id, fc.template_id, fc.pos, fc.case_edit_type, fcb.steps, fcb.text_description, fcb.expected_result, fcb.prerequisite, fcb.description FROM case_review_functional_case crfc LEFT JOIN functional_case fc ON crfc.case_id = fc.id LEFT JOIN functional_case_blob fcb ON fcb.id = fc.id LEFT JOIN (select * from (SELECT crh.case_id, crh.`status`, ROW_NUMBER() OVER (PARTITION BY crh.case_id ORDER BY crh.create_time DESC) as rn from case_review_history crh where crh.review_id = #{request.reviewId} and crh.deleted = false
AND crh.create_user = #{viewStatusUserId}
AND crh.create_user = null
order by crh.create_time desc ) as history_filter WHERE rn = 1 ) as history ON history.case_id = crfc.case_id WHERE crfc.review_id = #{request.reviewId} AND fc.deleted = #{deleted}
AND crfc.case_id in (select crfcu.case_id from case_review_functional_case_user crfcu where crfcu.review_id = #{request.reviewId} and crfcu.user_id = #{userId})
AND fc.project_Id = #{request.projectId} AND fc.module_id = #{request.moduleId}

SELECT tpfc.id as id, history.`status` as reviewStatus, fc.module_id as moduleId, fc.name as name, fc.id as caseId, fc.project_id, fc.template_id, fc.pos, fc.case_edit_type, IFNULL(history.`steps`, fcb.steps) as steps, execute_history.`steps` as executeSteps, fcb.text_description, fcb.expected_result, fcb.prerequisite, fcb.description, history.`content` FROM test_plan_functional_case tpfc LEFT JOIN functional_case fc ON tpfc.functional_case_id = fc.id LEFT JOIN functional_case_blob fcb ON fcb.id = fc.id LEFT JOIN (select * from (SELECT tpceh.test_plan_case_id, tpceh.`status`,tpceh.`steps`, tpceh.`content`, ROW_NUMBER() OVER (PARTITION BY tpceh.test_plan_case_id ORDER BY tpceh.create_time DESC) as rn from test_plan_case_execute_history tpceh where tpceh.test_plan_id = #{request.planId} and tpceh.deleted = false
order by tpceh.create_time desc ) as history_filter WHERE rn = 1 ) as history ON history.test_plan_case_id = tpfc.id LEFT JOIN (select * from (SELECT tpceh.test_plan_case_id, tpceh.`status`,tpceh.`steps`, ROW_NUMBER() OVER (PARTITION BY tpceh.test_plan_case_id ORDER BY tpceh.create_time DESC) as rn from test_plan_case_execute_history tpceh where tpceh.test_plan_id = #{request.planId} and tpceh.deleted = false and tpceh.steps IS NOT NULL
order by tpceh.create_time desc ) as history_filter WHERE rn = 1 ) as execute_history ON execute_history.test_plan_case_id = tpfc.id WHERE tpfc.test_plan_id = #{request.planId} AND fc.deleted = #{deleted} AND fc.project_Id = #{request.projectId} AND fc.module_id = #{request.moduleId}

select
functional_case.id,
functional_case.create_user
from functional_case
where functional_case.deleted = false
and functional_case.project_id = #{projectId}

    AND functional_case.id not in (
    select test_plan_functional_case.functional_case_id from test_plan_functional_case where test_plan_functional_case.test_plan_id = #{testPlanId}
    )

order by functional_case.pos asc

select
functional_case.id,
functional_case.create_user,
functional_case.pos
from functional_case
where functional_case.deleted = false
and functional_case.project_id = #{projectId}
and functional_case.module_id in

    #{moduleId}


    AND functional_case.id not in (
    select functional_case_id from test_plan_functional_case where test_plan_id = #{testPlanId}
    )
