mapper.select:
select distinct graph_id
from functional_case_relationship_edge
where source_id in

    #{id}

or target_id in

    #{id}

SELECT fcre.id id, fc.NAME name, fc.version_id versionId, pv.`name` versionName, u.`name` userName, fc.num, fc.id caseId FROM functional_case_relationship_edge fcre
LEFT JOIN functional_case fc ON fcre.target_id = fc.id
LEFT JOIN functional_case fc ON fcre.source_id = fc.id
LEFT JOIN `user` u ON fc.create_user = u.id LEFT JOIN project_version pv ON pv.id = fc.version_id WHERE fc.deleted = false
AND fcre.source_id = #{request.id}
AND fcre.target_id = #{request.id}
order by
fc.${sort}
fcre.create_time desc

mapper.update:
update functional_case_relationship_edge set graph_id = #{graphId} where (source_id in

    #{id}

or target_id in

    #{id}
)
