mapper.select:
SELECT id FROM functional_case_test where functional_case_test.case_id = #{request.caseId}
AND
and (
)
1 = 1

SELECT fct.id, fct.case_id, fct.source_id, fct.source_type, fct.project_id, fct.version_id, fct.create_time, p.name as projectName, v.name as versionName, IF(atc.name is not null, atc.name, scenario.name) as sourceName, IF(atc.num is not null, atc.num, scenario.num) as sourceNum FROM functional_case_test fct LEFT JOIN project p ON fct.project_id = p.id LEFT JOIN project_version v ON fct.version_id = v.id LEFT JOIN api_test_case atc ON fct.source_id = atc.id LEFT JOIN api_scenario scenario ON fct.source_id = scenario.id WHERE fct.case_id = #{request.sourceId} AND ( atc.deleted = false OR scenario.deleted = false )
AND fct.source_type = #{request.sourceType}
AND fct.project_id = #{request.projectId}
and ( atc.name like concat('%', #{request.keyword},'%') or scenario.name like concat('%', #{request.keyword},'%') )
order by fct.create_time desc

SELECT tpceh.id, tpceh.test_plan_case_id, tpceh.case_id, tpceh.status, tpceh.notifier, tpceh.create_user, tpceh.create_time, tpceh.content, tpceh.deleted, tpceh.steps, u.name as userName, ux.avatar as userLogo, tp.name as testPlanName, tp.id as testPlanId, u.email as email, functional_case.case_edit_type as caseEditType FROM test_plan_case_execute_history tpceh left join functional_case on tpceh.case_id = functional_case.id left join user u on tpceh.create_user = u.id left join user_extend ux on tpceh.create_user = ux.id left join test_plan tp on tpceh.test_plan_id = tp.id where tpceh.case_id = #{caseId}
and tpceh.test_plan_case_id = #{planId}
order by tpceh.create_time desc

