mapper.select:
SELECT * FROM environment_group WHERE environment_group.project_id = #{projectId}
AND ( id = #{keyword} OR (name LIKE CONCAT('%', #{keyword}, '%')) )
ORDER BY pos asc

SELECT pos
FROM environment
WHERE project_id = #{projectId}
ORDER BY pos DESC
LIMIT 1;

select `pos` from environment where project_id = #{projectId}

    and `pos` < #{basePos}

order by `pos` desc limit 1;

