mapper.select:
SELECT *
FROM exec_task_item


        and (
        exec_task_item.task_id like concat('%', #{request.keyword},'%')
        or exec_task_item.resource_name like concat('%', #{request.keyword},'%')
        )


        and exec_task_item.organization_id = #{orgId}


        and exec_task_item.project_id = #{projectId}
