mapper.select:
select o.id, coalesce(membercount, 0) as memberCount, coalesce(projectcount, 0) as projectCount from organization o left join ( select source_id, count(distinct u.id) as membercount from user_role_relation ur join user u on ur.user_id = u.id and u.deleted = 0 where source_id in
#{id}
group by source_id ) as members_group on o.id = members_group.source_id left join ( select organization_id, count(project.id) as projectcount from project where organization_id in
#{id}
group by organization_id ) as projects_group on o.id = projects_group.organization_id

mapper.update:
update organization
set deleted     = true,
    delete_time = #{request.deleteTime},
    delete_user = #{request.deleteUserId}
where id = #{request.organizationId}

update organization
set deleted = false
where id = #{id}
