mapper.select:
select task.*, QRTZ_TRIGGERS.TRIGGER_NAME as triggerName from (
SELECT schedule.id, schedule.name as taskname, schedule.project_id, resource_type, ads.num as resource_num, ads.name as resource_name, ads.id as resource_id, schedule.value, schedule.enable, schedule.create_user AS createUserName, schedule.create_time FROM schedule inner join api_definition_swagger ads on schedule.resource_id = ads.id
SELECT schedule.id, schedule.name as taskname, schedule.project_id, resource_type, api_scenario.num as resource_num, api_scenario.name as resource_name, api_scenario.id as resource_id, schedule.value, schedule.enable, schedule.create_user AS createUserName, schedule.create_time FROM schedule inner join api_scenario on schedule.resource_id = api_scenario.id
) task left join project on task.project_id = project.id left join QRTZ_TRIGGERS on task.resource_id = QRTZ_TRIGGERS.TRIGGER_NAME where task.resource_type =#{request.scheduleTagType} and task.project_id IN
#{projectId}
and ( task.resource_name like concat('%', #{request.keyword},'%') or task.resource_num like concat('%', #{request.keyword},'%') )

select count(*)
from schedule
where id = #{id}
  and enable = #{enable}

SELECT count(*)
FROM schedule
WHERE resource_id = #{0}

select task.* from (
SELECT schedule.id, schedule.name as taskname, schedule.project_id, resource_type, ads.num as resource_num, ads.name as resource_name, ads.id as resource_id, schedule.value, schedule.enable, schedule.create_user AS createUserName, schedule.create_time FROM schedule inner join api_definition_swagger ads on schedule.resource_id = ads.id
SELECT schedule.id, schedule.name as taskname, schedule.project_id, resource_type, api_scenario.num as resource_num, api_scenario.name as resource_name, api_scenario.id as resource_id, schedule.value, schedule.enable, schedule.create_user AS createUserName, schedule.create_time FROM schedule inner join api_scenario on schedule.resource_id = api_scenario.id
) task left join project on task.project_id = project.id where task.resource_type =#{request.scheduleTagType} and task.project_id IN
#{projectId}
and ( task.resource_name like concat('%', #{request.condition.keyword},'%') or task.resource_num like concat('%', #{request.condition.keyword},'%') )
