mapper.select:
select id, name from project
and organization_id = #{organizationId}
order by create_time desc

select distinct u.id, u.NAME, u.email from `user` u left join user_role_relation urr on urr.user_id = u.id where u.deleted = 0 and urr.source_id = #{organizationId}
and (LOCATE(#{keyword},u.name)>0 or LOCATE(#{keyword},u.email)>0)
order by u.create_time desc limit 100

select p.id, count(distinct temp.id) as memberCount FROM project p LEFT JOIN (SELECT ur.source_id, u.id from user_role_relation ur left join user u on u.id = ur.user_id
ur.source_id in
#{projectId}
) temp on p.id = temp.source_id group by p.id
