mapper.select:
SELECT u.id as id, u.name as name, u.email as email, ue.avatar as avatar FROM user u LEFT JOIN user_extend ue ON u.id = ue.id LEFT JOIN user_role_relation urr ON u.id = urr.user_id WHERE urr.role_id IN (SELECT urp.role_id FROM user_role_permission urp WHERE urp.permission_id = #{permission}) AND urr.source_id = #{projectId}
AND (name LIKE concat('%', #{keyword}, '%') OR email LIKE concat('%', #{keyword}, '%'))
GROUP BY urr.user_id limit 1000

SELECT u.id as id, u.name as name, u.email as email, ue.avatar as avatar FROM user u LEFT JOIN user_extend ue ON u.id = ue.id LEFT JOIN user_role_relation urr ON u.id = urr.user_id WHERE urr.source_id = #{projectId}
AND (name LIKE concat('%', #{keyword}, '%') OR email LIKE concat('%', #{keyword}, '%'))
and u.deleted = false GROUP BY urr.user_id limit 1000

mapper.update:
UPDATE user
SET password = MD5(#{password})
WHERE id = #{userId}
