mapper.update:
UPDATE
    test_plan_api_case
SET pos =#{pos}
WHERE id = #{id}

mapper.select:
SELECT max(pos)
FROM test_plan_api_case
WHERE test_plan_id = #{0}

SELECT id
FROM api_test_case
WHERE deleted = false

    AND id IN

        #{item}



    AND module_id IN

        #{item}



    AND id NOT IN
    (SELECT api_case_id FROM test_plan_api_case WHERE test_plan_id = #{testPlanId})


    ORDER BY #{orderString}

SELECT id, pos
FROM test_plan_api_case
WHERE id = #{0}

SELECT id, pos
FROM test_plan_api_case
WHERE test_plan_id = #{parentId}

    AND pos > #{pos}


    AND pos < #{pos}

ORDER BY pos

    DESC

LIMIT 1
