mapper.update:
UPDATE
    test_plan_api_scenario
SET pos =#{pos}
WHERE id = #{id}

mapper.select:
SELECT max(pos)
FROM test_plan_api_scenario
WHERE test_plan_id = #{0}

SELECT id
FROM api_scenario
WHERE deleted = false

    AND id IN

        #{item}



    AND module_id IN

        #{item}



    AND id NOT IN
    (SELECT api_scenario_id FROM test_plan_api_scenario WHERE test_plan_id = #{testPlanId})


    ORDER BY #{orderString}

SELECT id, pos
FROM test_plan_api_scenario
WHERE id = #{0}

SELECT id, pos
FROM test_plan_api_scenario
WHERE test_plan_id = #{parentId}

    AND pos > #{pos}


    AND pos < #{pos}

ORDER BY pos

    DESC

LIMIT 1

select last_exec_result as result, count(id) as resultCount
from test_plan_api_scenario
where test_plan_id = #{0}
group by last_exec_result;
