mapper.select:
SELECT id, parent_id AS parentId
FROM test_plan_module
WHERE project_id = #{projectId}

SELECT max(pos)
FROM test_plan_module
WHERE parent_id = #{parentId}

SELECT id, name, pos, project_Id, parent_id
FROM test_plan_module
WHERE id = #{0}

SELECT id, name, pos, project_Id, parent_id
FROM test_plan_module
WHERE parent_id = #{parentId}

    AND pos > #{pos}


    AND pos < #{pos}

ORDER BY pos

    DESC

LIMIT 1

SELECT id
FROM test_plan_module
WHERE project_id = #{0}

SELECT name
FROM test_plan_module
WHERE id = #{0}

SELECT project_id
FROM test_plan_module
WHERE id = #{0}

SELECT id, name, parent_id AS parentId, 'module' AS type FROM test_plan_module WHERE id IN
#{id}
ORDER BY pos
