mapper.select:
select ifnull(tprac.api_case_execute_result, 'PENDING') as status, count(id) as count from test_plan_report_api_case tprac
where tprac.test_plan_report_id = #{id}
group by tprac.api_case_execute_result

select tpac.id as testPlanApiCaseId, atc.id as apiCaseId, atc.num as apiCaseNum, atc.name as apiCaseName, atc.priority as apiCasePriority, tpac.test_plan_collection_id testPlanCollectionId, tpac.environment_id as environmentId, if(ad.module_id = 'root','未规划用例', ad.module_id) as apiCaseModule, tpac.execute_user as apiCaseExecuteUser, ifnull(tpac.last_exec_result, 'PENDING') as apiCaseExecuteResult, tpac.last_exec_report_id apiCaseExecuteReportId, tpac.pos as pos from test_plan_api_case tpac join api_test_case atc on atc.id = tpac.api_case_id left join api_definition ad on atc.api_definition_id = ad.id left join api_definition_module adm on ad.module_id = adm.id where tpac.test_plan_id = #{id} and atc.deleted = false and tpac.id in
#{id}
group by tpac.id

select adm.id, adm.name from api_definition_module adm
where adm.id in

    #{id}

select tprac.api_case_id as id, tprac.api_case_num as num, tprac.api_case_name as name, tprac.test_plan_name as planName, tprac.api_case_module as moduleName, tprac.api_case_priority as priority, tprac.api_case_execute_report_id reportId, ifnull(tprac.api_case_bug_count, 0) as bugCount, ifnull(tprac.api_case_execute_result, 'PENDING') as executeResult, tprac.api_case_execute_user as executeUser, atc.project_id projectId from test_plan_report_api_case tprac left join api_test_case atc on tprac.api_case_id = atc.id where tprac.test_plan_report_id = #{request.reportId}
and (tprac.api_case_num like concat('%', #{request.keyword}, '%') or tprac.api_case_name like concat('%', #{request.keyword}, '%'))
and tprac.test_plan_collection_id = #{request.collectionId}

select id from test_plan_report_api_case
where test_plan_report_id = #{testPlanReportId} and test_plan_collection_id = #{collectionId}
order by pos desc
