mapper.select:
select tprac.api_case_execute_result as status, count(id) as count from test_plan_report_api_case tprac
where tprac.test_plan_report_id = #{id}
group by tprac.api_case_execute_result

select tpac.id as testPlanApiCaseId, atc.id as apiCaseId, atc.num as apiCaseNum, atc.name as apiCaseName, atc.priority as apiCasePriority, if(ad.module_id = 'root','未规划用例', ad.module_id) as apiCaseModule, tpac.execute_user as apiCaseExecuteUser, ifnull(tpac.last_exec_result, 'PENDING') as apiCaseExecuteResult from test_plan_api_case tpac join api_test_case atc on atc.id = tpac.api_case_id left join api_definition ad on atc.api_definition_id = ad.id left join api_definition_module adm on ad.module_id = adm.id where atc.deleted = false and tpac.test_plan_id in
#{id}
group by tpac.id
