mapper.select:
select id,report_id,api_name,start_time,response_time,url
from api_data_view


        url = #{url}


        AND date_format(start_time,'%Y-%m-%d %H:%i:%s')
        between #{startTime} and #{endTime}


        AND TO_DAYS(start_time) =TO_DAYS(NOW())

    order by start_time;

select id,report_id,api_name,start_time,response_code,url from api_data_view
url = #{url}
AND date_format(start_time,'%Y-%m-%d %H:%i:%s') between #{startTime} and #{endTime} and length(response_code)=3
AND TO_DAYS(start_time) =TO_DAYS(NOW()) and length(response_code)=3
order by start_time;
