mapper.select:
select issues.id, issues.title , issues.num , test_case_issues.resource_id, test_case_issues.ref_id, test_case_issues.ref_type from issues inner join test_case_issues on test_case_issues.issues_id = issues.id where (issues.platform_status != 'delete' or issues.platform_status is NULL)
and test_case_issues.resource_id in
#{item}
or test_case_issues.ref_id in
#{item}
and test_case_issues.resource_id in
#{item}
order by num asc

select count(issues.id) as totalIssueCount, ifnull(issues.status,issues.platform_status) as status from issues
left join project on issues.project_id = project.id

group by status;

select id,platform, platform_id
from issues
where project_id = #{projectId} and platform != 'Local' and (platform_status != 'delete' or platform_status is null);

select id from issues
where project_id = #{projectId} and platform = #{platform} and platform_id not in

    #{value}

select id, custom_fields
from issues
where project_id = #{projectId}
limit #{offset},#{pageSize}

