mapper.update:
update ${tableName} set project_id = #{record.projectId,jdbcType=VARCHAR}, `name` = #{record.name,jdbcType=VARCHAR},
`module_path` = #{record.modulePath,jdbcType=VARCHAR},
parent_id = #{record.parentId,jdbcType=VARCHAR}, `level` = #{record.level,jdbcType=INTEGER}, create_time = #{record.createTime,jdbcType=BIGINT}, update_time = #{record.updateTime,jdbcType=BIGINT}, pos = #{record.pos,jdbcType=DOUBLE}, create_user = #{record.createUser,jdbcType=VARCHAR} where id = #{record.id,jdbcType=VARCHAR}

mapper.select:
select

from ${tableName}
where project_id = #{projectId}
order by pos asc

select

from ${tableName}
where project_id = #{projectId} and `scenario_type` = #{scenarioType}
order by pos asc

select

from ${tableName}
where id IN

    #{id}

order by pos asc

SELECT *
FROM ${tableName}
WHERE project_id = #{projectId}
  AND module_path = #{modulePath}
