mapper.select:
SELECT p.id, p.workspace_id, p.name, p.description, p.update_time, p.issue_template_id, p.case_template_id, p.create_time, w.id AS workspaceId, w.name AS workspaceName, p.tapd_id, p.jira_key, p.zentao_id, p.azure_devops_id, p.azure_filter_id, user.name AS createUserName, p.platform, p.third_part_template, p.issue_config FROM project p JOIN workspace w ON p.workspace_id = w.id LEFT JOIN user ON user.id = p.create_user
AND p.create_user IN
#{value}
AND p.id IN
#{value}
AND p.workspace_id IN
#{value}
AND p.name LIKE #{proRequest.name, jdbcType=VARCHAR}
AND w.id = #{proRequest.workspaceId}
AND p.id = #{proRequest.projectId}
ORDER BY
p.${order.name} ${order.type}

SELECT id
FROM project
WHERE workspace_id = #{workspaceId}

SELECT DISTINCT p.*
FROM `group` g
             JOIN user_group ug ON g.id = ug.group_id
             JOIN project p ON p.id = ug.source_id

    g.type = 'PROJECT'
            AND ug.user_id = #{proRequest.userId}

        AND p.workspace_id = #{proRequest.workspaceId}


        AND p.name LIKE #{proRequest.name, jdbcType=VARCHAR}

SELECT system_id
FROM project
WHERE id = #{0}

mapper.update:
update project set case_template_id = #{templateId}
where (case_template_id = #{originId} or case_template_id is null or case_template_id = '')
and id = #{projectId}
