mapper.select:
SELECT t.id,t.name,'API' AS executionModule, t.report_type, ifnull(t2.name,'LOCAL') AS actuator, t1.`name` AS executor,t.create_time AS executionTime, ifnull(t.trigger_mode,'MANUAL') AS triggerMode ,ifnull(t.status,'Saved') AS executionStatus FROM api_definition_exec_result t INNER JOIN `user` t1 ON t.user_id = t1.id LEFT JOIN test_resource_pool t2 ON t.actuator = t2.id WHERE to_days(FROM_UNIXTIME(t.create_time/1000))= to_days(now()) AND t.project_id IN
#{id}
AND t.trigger_mode in ('JENKINS_API_PLAN', 'JENKINS_SCENARIO_PLAN', 'JENKINS_PERFORMANCE_TEST', 'JENKINS')
AND t.status = #{request.executionStatus}
AND t.user_id = #{request.executor}
AND (t.integrated_report_id IS NULL OR t.integrated_report_id = 'null') ORDER BY t.create_time DESC

SELECT t.id,t.name AS name ,'SCENARIO' AS executionModule,t.report_type, ifnull(t2.name,'LOCAL') AS actuator, t1.`name` AS executor,t.create_time AS executionTime, t.trigger_mode AS triggerMode ,t.status AS executionStatus FROM api_scenario_report t INNER JOIN `user` t1 ON t.user_id = t1.id LEFT JOIN test_resource_pool t2 ON t.actuator = t2.id WHERE to_days(FROM_UNIXTIME(t.create_time/1000))= to_days(now()) AND t.execute_type !='Debug' AND t.execute_type !='Marge' AND t.project_id IN
#{id}
AND t.trigger_mode in ('JENKINS_API_PLAN', 'JENKINS_SCENARIO_PLAN', 'JENKINS_PERFORMANCE_TEST', 'JENKINS')
AND t.status = #{request.executionStatus}
AND t.user_id = #{request.executor}
ORDER BY t.create_time DESC

SELECT t.id,t.name,'PERFORMANCE' AS executionModule,'PERFORMANCE' AS report_type, ifnull(t2.name,'LOCAL') AS actuator, t1.`name` AS executor,t.create_time AS executionTime, t.trigger_mode AS triggerMode ,t.`status` AS executionStatus FROM load_test_report t INNER JOIN `user` t1 ON t.user_id = t1.id LEFT JOIN test_resource_pool t2 ON t.test_resource_pool_id = t2.id WHERE to_days(FROM_UNIXTIME(t.create_time/1000))= to_days(now()) AND t.project_id IN
#{id}
AND t.trigger_mode in ('JENKINS_API_PLAN', 'JENKINS_SCENARIO_PLAN', 'JENKINS_PERFORMANCE_TEST', 'JENKINS')
AND t.status = #{request.executionStatus}
AND t.user_id = #{request.executor}
ORDER BY t.create_time DESC

SELECT ( SELECT count( t.id ) FROM api_scenario_report t LEFT JOIN `user` t1 ON t.user_id = t1.id LEFT JOIN test_resource_pool t2 ON t.actuator = t2.id WHERE to_days(FROM_UNIXTIME( t.create_time / 1000 ))= to_days(now()) AND t.execute_type != 'Debug' AND t.execute_type != 'Marge' AND t.project_id IN
#{id}
AND t.trigger_mode in ('JENKINS_API_PLAN', 'JENKINS_SCENARIO_PLAN', 'JENKINS_PERFORMANCE_TEST', 'JENKINS')
AND t.status = #{request.executionStatus}
AND t.user_id = #{request.executor}
AND t.status IN ("running","starting","waiting") ) as scenarioTotal , (SELECT count( t.id ) FROM api_definition_exec_result t LEFT JOIN `user` t1 ON t.user_id = t1.id LEFT JOIN test_resource_pool t2 ON t.actuator = t2.id WHERE to_days( FROM_UNIXTIME( t.create_time / 1000 ))= to_days(now()) AND t.project_id IN
#{id}
AND t.trigger_mode in ('JENKINS_API_PLAN', 'JENKINS_SCENARIO_PLAN', 'JENKINS_PERFORMANCE_TEST', 'JENKINS')
AND t.status = #{request.executionStatus}
AND t.user_id = #{request.executor}
AND (t.integrated_report_id IS NULL OR t.integrated_report_id = 'null') AND t.status IN ("running","starting","waiting") ) as apiTotal , (SELECT count( t.id ) FROM load_test_report t LEFT JOIN `user` t1 ON t.user_id = t1.id LEFT JOIN test_resource_pool t2 ON t.test_resource_pool_id = t2.id WHERE to_days(FROM_UNIXTIME( t.create_time / 1000 ))= to_days(now()) AND t.project_id IN
#{id}
AND t.trigger_mode in ('JENKINS_API_PLAN', 'JENKINS_SCENARIO_PLAN', 'JENKINS_PERFORMANCE_TEST', 'JENKINS')
AND t.status = #{request.executionStatus}
AND t.user_id = #{request.executor}
AND t.status IN ("running","starting","waiting") ) as perfTotal

