select dateStr, ifnull(tt.num,0) countNum
     from
    (
     select adddate('2019-01-01',t4.i*10000 + t3.i*1000 + t2.i*100 + t1.i*10 + t0.i) dateStr from
     (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t0,
     (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t1,
     (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t2,
     (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t3,
     (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t4
    ) v
    left join
        (select FROM_UNIXTIME(t.create_time/1000, '%Y-%m-%d')  as create_times,t.project_id,t.node_id,t.priority,count(id) num ,t.maintainer from test_case t
         WHERE project_id in

             #{id}


               and node_id in

                    #{id}



                and priority in

                    #{p}



                and maintainer in

                    #{p}


          GROUP BY  create_times
      ) tt
      on dateStr = tt.create_times
     where dateStr BETWEEN  #{startTime} and #{endTime}

        order  by dateStr


        order by countNum ${order}

select dateStr, ifnull(tt.num,0) countNum from ( select adddate('2019-01-01',t4.i*10000 + t3.i*1000 + t2.i*100 + t1.i*10 + t0.i) dateStr from (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t0, (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t1, (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t2, (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t3, (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t4 ) v left join (select FROM_UNIXTIME(t.update_time/1000, '%Y-%m-%d') as update_times,t.project_id,t.node_id,t.priority,t.create_time,t.update_time,count(id) num ,t.maintainer from test_case t WHERE create_time!= update_time and project_id in
#{id}
and node_id in
#{id}
and priority in
#{id}
and maintainer in
#{p}
GROUP BY update_times ) tt on dateStr = tt.update_times where dateStr BETWEEN #{startTime} and #{endTime}
order by dateStr
order by countNum ${order}
