SELECT COUNT(1)
FROM test_case, project
WHERE test_case.project_id = project.id

    AND project.id IN

        #{id}


AND test_case.id = #{caseId}

select test_case.id from (select test_case.id,test_case.update_time, test_case.ref_id , test_case.`status` , test_case.case_public , test_case.project_id from test_case left join project on test_case.project_id = project.id) test_case right join (select max(test_case.update_time) update_time , ref_id from test_case left join (select id,workspace_id,NAME from project where workspace_id =#{request.workspaceId}) project on test_case.project_id = project.id where (test_case.STATUS IS NULL OR test_case.STATUS != 'Trash') and test_case.case_public=true GROUP BY ref_id) tmp on test_case.update_time = tmp.update_time and test_case.ref_id = tmp.ref_id
and (test_case.status is null or test_case.status != #{request.statusIsNot})
and (test_case.status is null or test_case.status != #{request.notEqStatus})
and (test_case.name like CONCAT('%', #{request.name},'%') or test_case.num like CONCAT('%', #{request.name},'%') or test_case.tags like CONCAT('%', #{request.name},'%') or test_case.custom_num like CONCAT('%', #{request.name},'%'))
and test_case.id in
#{id}
and test_case.id in (select test_case_id from test_case_test where test_case_test.create_time >= #{request.createTime})
and test_case.create_time >= #{request.createTime}
and test_case.node_id in
#{nodeId}
AND test_case.project_id in (select id from project where workspace_id=#{request.workspaceId})
and test_case.project_id = #{request.projectId}
and test_case.id not in (select distinct test_case_test.test_case_id from test_case_test)
and test_case.id in (select distinct test_case_test.test_case_id from test_case_test)
and test_case.version_id = #{request.versionId}
and test_case.ref_id = #{request.refId}
and test_case.case_public = TRUE

SELECT count(distinct test_case.ref_id) AS countNumber FROM test_case join test_case_test on test_case.id = test_case_test.test_case_id
WHERE test_case.project_id = #{projectId} and test_case.status != 'Trash' and latest = 1
AND test_case_test.create_time BETWEEN #{firstDayTimestamp} AND #{lastDayTimestamp}

