mapper.select:
select test_case.id, test_case.name, test_case.priority, test_case.type, test_case.review_status, test_case.num, test_case.custom_num, test_case.priority, test_case.tags, test_case.create_time, test_case.last_execute_result, test_case.update_time, project_version.name versionName, test_case.version_id versionId from test_case left join project_version on project_version.project_id = test_case.project_id and project_version.id = test_case.version_id
left join test_plan_test_case as T2 on test_case.id=T2.case_id and T2.plan_id =#{request.planId}
and T2.case_id is null

select test_case.id, test_case.name, test_case.priority, test_case.type, test_case.review_status,test_case.num,test_case.custom_num from test_case
and test_case.name like CONCAT('%', #{request.name},'%')
AND test_case.project_id = #{request.projectId}
AND test_case.node_id IN
#{nodeId}
ORDER BY test_case.update_time DESC

select load_test.id, load_test.name, load_test.project_id,'性能测试' as type, project_version.name as version_name from load_test inner join project_version on project_version.project_id = load_test.project_id and project_version.id = load_test.version_id
and load_test.project_id= #{request.projectId}
UNION ALL select api_scenario.id, api_scenario.name, api_scenario.project_id,'接口场景' as type, project_version.name as version_name from api_scenario inner join project_version on project_version.project_id = api_scenario.project_id and project_version.id = api_scenario.version_id
and api_scenario.project_id= #{request.projectId}
and api_scenario.status!='Trash' UNION ALL select b.id,b.name,b.project_id,'接口用例' as type, project_version.name as version_name from api_test_case b inner join api_definition a on b.api_definition_id = a.id and a.status != 'Trash' inner join project_version on project_version.project_id = a.project_id and project_version.id = a.version_id
and b.project_id= #{request.projectId}

