mapper.update:
update test_case_review_load tplc
set status = #{status}
where tplc.load_report_id = #{reportId}

mapper.select:
select load_test.id
from load_test
where load_test.project_id = #{projectId}
  and load_test.id not in (
    select tplc.load_case_id from test_case_review_load tplc where tplc.test_case_review_id = #{reviewId}
)

select tplc.id, u.name as userName, tplc.create_time, tplc.update_time, tplc.test_case_review_id, tplc.load_case_id, lt.status, lt.num, tplc.status as caseStatus, lt.name as caseName, tplc.load_report_id, p.name as projectName from test_case_review_load tplc inner join load_test lt on tplc.load_case_id = lt.id inner join user u on lt.user_id = u.id inner join project p on lt.project_id = p.id
tplc.test_case_review_id = #{request.testCaseReviewId}
and lt.project_id = #{request.projectId}
and (lt.name like CONCAT('%', #{request.name},'%') or lt.num like CONCAT('%', #{request.name},'%'))
and tplc.status like CONCAT('%', #{request.status},'%')
and lt.status in
#{value}
order by
tplc.${order.name} ${order.type}
