mapper.insert:
INSERT INTO test_plan_api_case(id, test_plan_id, api_case_id, environment_id, create_time, update_time, create_user)
SELECT #{request.id}, #{request.testPlanId}, #{request.apiCaseId}, #{request.environmentId}, #{request.createTime}, #{request.updateTime}, #{request.createUser}
  FROM DUAL
  WHERE NOT EXISTS(
    SELECT id FROM
    test_plan_api_case
    WHERE test_plan_id = #{request.testPlanId} and api_case_id = #{request.apiCaseId}
  )

mapper.select:
SELECT t.* FROM api_test_case t
INNER JOIN test_plan_api_case tpac ON t.id = tpac.api_case_id
WHERE tpac.id = #{0}

SELECT t.* FROM test_plan_api_case t WHERE t.test_plan_id = #{0}
AND t.api_case_id IN (
    SELECT id FROM api_test_case WHERE status IS NULL OR status != 'Trash'
    )

select
t.id, t.environment_id,(select name from api_test_environment where id = t.environment_id) as environment_name, t.create_time, t.update_time,
c.id as case_id, c.project_id, c.name, c.api_definition_id, c.priority, c.description, c.create_user_id,
c.update_user_id, c.num, c.tags, t.create_user,
a.module_id, a.path, a.protocol, t.status execResult, a.user_id
from
test_plan_api_case t
inner join
api_test_case c
on t.api_case_id = c.id

    and t.test_plan_id = #{request.planId}

inner join
  api_definition a
on
  c.api_definition_id = a.id

  and a.protocol = #{request.protocol}



    and c.status = 'Trash'


    and (c.status IS NULL or c.status != 'Trash')


    and (c.status IS NULL or c.status != 'Trash')


    and t.status IS NULL


    and t.status = #{request.status}


where 1


    and a.projectId = #{request.projectId}

  and t.id in

    #{caseId}



  and (c.name like CONCAT('%', #{request.name},'%') or c.tags like CONCAT('%', #{request.name},'%'))


  and a.module_id in

    #{nodeId}







          and c.priority in

            #{value}



          and c.create_user_id in

            #{value}



          and a.priority in

            #{value}







  order by



        t.${order.name} ${order.type}


        create_user_id ${order.type}


        ${order.name} ${order.type}

select
t.id
from
test_plan_api_case t
inner join
api_test_case c
on t.api_case_id = c.id

  and t.test_plan_id = #{request.planId}

inner join
api_definition a
on
c.api_definition_id = a.id

  and a.protocol = #{request.protocol}



    and a.status = 'Trash'


    and a.status != 'Trash'


    and a.status != 'Trash'


    and t.status IS NULL


    and t.status = #{request.status}


where 1


    and

  t.id in

    #{caseId}



  and (c.name like CONCAT('%', #{request.name},'%') or c.tags like CONCAT('%', #{request.name},'%'))


  and a.module_id in

    #{nodeId}







          and c.priority in

            #{value}



          and c.create_user_id in

            #{value}







  order by



        t.${order.name} ${order.type}


        create_user_id ${order.type}


        ${order.name} ${order.type}

select id
from test_plan_api_case
where id = #{planId}

select t.id from test_plan_api_case t inner join api_test_case c on c.id = t.api_case_id
and c.project_id not in
#{projectId}
where t.test_plan_id = #{planId}

select id,status from test_plan_api_case where test_plan_id = #{planId} and api_case_id IN (
    SELECT id FROM api_test_case where status is null or status != 'Trash'
  )

select t.id, c.id as case_id, c.project_id, c.name, c.api_definition_id, c.priority, c.create_user_id, c.num,t.create_user, t.status execResult from test_plan_api_case t inner join api_test_case c on t.api_case_id = c.id and t.test_plan_id = #{planId}
and t.status = 'error'
and c.status != 'Trash' where t.test_plan_id = #{planId};

select t.id, c.id as case_id, c.project_id, c.name, c.api_definition_id, c.priority, c.create_user_id, c.num,t.create_user, t.status execResult from test_plan_api_case t inner join api_test_case c on t.api_case_id = c.id and t.test_plan_id = #{planId}
and t.status = 'error'
