mapper.insert:
-- 查询没有数据再插入
INSERT INTO test_plan_api_scenario(id, test_plan_id, api_scenario_id, create_time, update_time, environment)
SELECT #{request.id}, #{request.testPlanId}, #{request.apiScenarioId}, #{request.createTime}, #{request.updateTime}, #{request.environment}
  FROM DUAL
  WHERE NOT EXISTS(
    SELECT id FROM
    test_plan_api_scenario
    WHERE test_plan_id = #{request.testPlanId} and api_scenario_id = #{request.apiScenarioId}
  )

mapper.select:
select t.id, t.environment, t.create_time, t.update_time, t.last_result, t.pass_rate, t.report_id, c.scenario_definition, c.id as case_id, c.user_id,c.api_scenario_module_id, c.module_path, c.name, c.level, c.status, c.principal, c.step_total, c.follow_people, c.schedule, c.description, c.tags, t.create_user, c.project_id, c.num, c.custom_num from test_plan_api_scenario t inner join api_scenario c on t.api_scenario_id = c.id and c.status != 'Trash'
and t.test_plan_id = #{request.planId}
where 1
and c.id in
#{caseId}
and c.project_id = #{request.projectId}
and t.id in
#{caseId}
and (c.name like CONCAT('%', #{request.name},'%') or c.num like CONCAT('%', #{request.name},'%') or c.custom_num like CONCAT('%', #{request.name},'%') or c.tags like CONCAT('%', #{request.name},'%'))
and t.last_result like CONCAT('%', #{request.status},'%')
and c.api_scenario_module_id in
#{nodeId}
order by
t.${order.name} ${order.type}
${order.name} ${order.type}

select t.id from test_plan_api_scenario t inner join api_scenario c on t.api_scenario_id = c.id and c.status != 'Trash'
and t.test_plan_id = #{request.planId}
left join project p on c.project_id = p.id left join user u on c.user_id = u.id where 1
and
t.id in
#{caseId}
and (c.name like CONCAT('%', #{request.name},'%') or c.num like CONCAT('%', #{request.name},'%') or c.tags like CONCAT('%', #{request.name},'%'))
and t.last_result like CONCAT('%', #{request.status},'%')
and c.api_scenario_module_id in
#{nodeId}
order by
t.${order.name} ${order.type}
${order.name} ${order.type}

select id
from test_plan_api_scenario
where test_plan_id = #{planId}

select t.id from test_plan_api_scenario t inner join api_scenario c on c.id = t.api_scenario_id
and c.project_id not in
#{projectId}
where t.test_plan_id = #{planId}
