mapper.insert:
-- 查询没有数据再插入
INSERT INTO test_plan_api_scenario(id, test_plan_id, api_scenario_id, create_time, update_time, environment, `order`, environment_group_id, environment_type)
SELECT #{request.id}, #{request.testPlanId}, #{request.apiScenarioId}, #{request.createTime}, #{request.updateTime}, #{request.environment}, #{request.order}, #{request.environmentGroupId}, #{request.environmentType}
  FROM DUAL
  WHERE NOT EXISTS(
    SELECT id FROM
    test_plan_api_scenario
    WHERE test_plan_id = #{request.testPlanId} and api_scenario_id = #{request.apiScenarioId}
  )

mapper.select:
SELECT t.* FROM test_plan_api_scenario t WHERE t.test_plan_id = #{0}
    AND t.api_scenario_id IN (
        SELECT id FROM api_scenario WHERE status IS NULL OR status != 'Trash'
    )
ORDER BY `order` DESC

SELECT t.id, t.environment, t.create_time, t.update_time, t.last_result, t.pass_rate, t.report_id, c.scenario_definition, c.id AS case_id, c.user_id, c.api_scenario_module_id, c.module_path, c. NAME, c. LEVEL, c. STATUS, c.principal, c.step_total, c. SCHEDULE, c.description, c.tags, c.create_user, c.project_id, c.num, c.custom_num, t.environment_group_id, t.environment_type, project_version. NAME versionName, project_version.id versionId FROM test_plan_api_scenario t INNER JOIN api_scenario c ON t.api_scenario_id = c.id INNER JOIN project_version ON c.project_id = project_version.project_id AND c.version_id = project_version.id AND c. STATUS != 'Trash'
and t.test_plan_id = #{request.planId}
WHERE 1
and c.id in
#{caseId}
and c.project_id = #{request.projectId}
and t.id in
#{caseId}
and (c.name like CONCAT('%', #{request.name},'%') or c.num like CONCAT('%', #{request.name},'%') or c.custom_num like CONCAT('%', #{request.name},'%') or c.tags like CONCAT('%', #{request.name},'%'))
and t.last_result like CONCAT('%', #{request.status},'%')
and c.api_scenario_module_id in
#{nodeId}
order by
t.${order.name} ${order.type}
${order.name} ${order.type}

select t.id from test_plan_api_scenario t inner join api_scenario c on t.api_scenario_id = c.id and c.status != 'Trash'
and t.test_plan_id = #{request.planId}
left join project p on c.project_id = p.id left join user u on c.user_id = u.id where 1
and
t.id in
#{caseId}
and (c.name like CONCAT('%', #{request.name},'%') or c.num like CONCAT('%', #{request.name},'%') or c.tags like CONCAT('%', #{request.name},'%'))
and t.last_result like CONCAT('%', #{request.status},'%')
and c.api_scenario_module_id in
#{nodeId}
order by
t.${order.name} ${order.type}
${order.name} ${order.type}

select id
from test_plan_api_scenario
where test_plan_id = #{planId}

select t.id from test_plan_api_scenario t inner join api_scenario c on c.id = t.api_scenario_id
and c.project_id not in
#{projectId}
where t.test_plan_id = #{planId}

select id,last_result as status, report_id, api_scenario_id as caseId from test_plan_api_scenario where test_plan_id = #{planId}
  and api_scenario_id IN (
    SELECT id FROM api_scenario where status is null or status != 'Trash'
  )

select t.id, t.last_result, t.report_id, c.user_id, c.module_path, c.name, c.level,c.create_user,c.principal, c.status,c.step_total, c.step_total, c.project_id, c.num, c.custom_num from test_plan_api_scenario t inner join api_scenario c on t.api_scenario_id = c.id and (c.status != 'Trash' or c.status is null) and t.test_plan_id = #{planId}
and t.last_result = #{status}
and (t.last_result in ('Stop','unExecute') or t.last_result IS NULL)
and t.last_result in ('Fail','Error')
where t.test_plan_id = #{planId} ORDER BY t.order DESC

select step_total from api_scenario c
where c.id in

  #{id}

SELECT project_id FROM test_plan WHERE id in (
  SELECT test_plan_id FROM test_plan_api_scenario WHERE id = #{0}
)

select `order` from test_plan_api_scenario where test_plan_id = #{planId}

  and `order` > #{baseOrder}

order by `order` desc limit 1;

select `order` from test_plan_api_scenario where test_plan_id = #{planId}

  and `order` < #{baseOrder}

order by `order` desc limit 1;
