mapper.select:
select count(t1.id) as `count`, t1.status
from test_plan_test_case t1
         inner join test_case t2
where t1.case_id = t2.id
  and plan_id = #{planId}
group by t1.status;

select test_plan_test_case.id as id, test_case.id as caseId, test_case.name, test_case.priority, test_case.type,test_case.test_id as testId,test_case.node_id, test_case.tags, test_case.maintainer, test_case.custom_fields, test_case.node_path, test_case.method, if(project.custom_num = 0, cast(test_case.num as char), test_case.custom_num) as customNum, test_plan_test_case.executor, test_plan_test_case.status, test_plan_test_case.actual_result, test_plan_test_case.update_time, test_plan_test_case.create_time,test_case_node.name as model, project.name as projectName,test_plan_test_case.issues as issues,test_plan_test_case.issues_count as issuesCount, test_plan_test_case.plan_id as planId from test_plan_test_case inner join test_case on test_plan_test_case.case_id = test_case.id left join test_case_node on test_case_node.id = test_case.node_id inner join project on project.id = test_case.project_id
order by
customNum ${order.type}
${order.name} ${order.type}

select test_plan_test_case.*, test_case.*
from test_plan_test_case
inner join test_case on test_plan_test_case.case_id = test_case.id


        and test_plan_test_case.plan_id = #{request.planId}


        and test_case.method = #{request.method}



        and test_case.node_path in

            #{nodePaths}

select test_plan_test_case.*, test_case.* from test_plan_test_case inner join test_case on test_plan_test_case.case_id = test_case.id
and test_plan_test_case.plan_id = #{request.planId}
and test_case.method = #{request.method}
and
test_case.node_path like concat('%',#{nodePaths},'%')

select distinct plan_id from test_plan_test_case
inner join test_plan
on test_plan_test_case.plan_id = test_plan.id

    test_plan_test_case.executor = #{userId}
    and test_plan.workspace_id = #{workspaceId}

        and test_plan.project_id = #{projectId}

select test_plan_test_case.*, test_case.* from test_plan_test_case inner join test_case on test_plan_test_case.case_id = test_case.id where test_plan_test_case.status != 'Prepare' and test_plan_test_case.status != 'Underway' and test_plan_test_case.Executor = #{request.executor} and test_plan_test_case.plan_id in
#{planId}
order by test_plan_test_case.update_time desc

select test_plan_test_case.*, test_case.* from test_plan_test_case inner join test_case on test_plan_test_case.case_id = test_case.id where (status = 'Prepare' or status = 'Underway') and test_plan_test_case.Executor = #{request.executor} and test_plan_test_case.plan_id in
#{planId}
order by test_plan_test_case.update_time asc

select status
from test_plan_test_case
where plan_id = #{planId}

select status
from test_plan_test_case
where plan_id = #{planId}

SELECT test_plan_api_case.id as reportId,test_plan_api_case.api_case_id as id,"definition" as type,api_test_case.name,test_plan_api_case.status from test_plan_api_case left join api_test_case on test_plan_api_case.api_case_id=api_test_case.id inner join api_definition a on api_test_case.api_definition_id = a.id and a.status != 'Trash'
and test_plan_api_case.test_plan_id = #{request.planId}
UNION ALL SELECT test_plan_api_scenario.api_scenario_id as testId,test_plan_api_scenario.id as id,"scenario" as type,api_scenario.name,test_plan_api_scenario.status from test_plan_api_scenario left join api_scenario on test_plan_api_scenario.api_scenario_id=api_scenario.id and api_scenario.status != 'Trash'
and test_plan_api_scenario.test_plan_id = #{request.planId}
UNION ALL select test_plan_load_case.id as reportId,load_test.id as id,"perform" as type,load_test.name as name,test_plan_load_case.status from test_plan_load_case inner join load_test on test_plan_load_case.load_case_id =load_test.id
and test_plan_load_case.test_plan_id = #{request.planId}
UNION ALL SELECT test_case.id as reportId,test_case.test_id as id,test_case.type as type,test_case.name,test_plan_test_case.status from test_plan_test_case left join test_case on test_plan_test_case.case_id =test_case.id
and test_plan_test_case.plan_id = #{request.planId}
and test_case.method = #{request.method}

select pc.id as id, pc.executor, pc.status, pc.update_time, pc.plan_id as planId, t.name, t.priority, t.type, t.node_id, t.steps, t.prerequisite, t.remark, t.node_path, t.method, t.num, t.step_model, t.expected_result, t.step_description from test_plan_test_case pc inner join test_case t on pc.case_id = t.id
pc.plan_id = #{request.planId} and t.node_id = #{request.nodeId}
order by pc.update_time desc;

mapper.update:
update test_plan_test_case


        status=#{reportStatus,jdbcType=VARCHAR}


where id in

    #{id}

update test_plan_test_case

    issues_count=#{count},
    issues = #{issues}

where
case_id=#{caseId,jdbcType=VARCHAR}

    and plan_id=#{id,jdbcType=VARCHAR}
