mapper.select:
select ug.user_id, ug.group_id, ug.source_id, g.`name`, g.type
from user_group ug join `group` g on ug.group_id = g.id
where ug.user_id = #{userId}

    and ug.source_id = #{projectId}

SELECT DISTINCT * FROM ( SELECT `user`.* FROM user_group JOIN `user` ON user_group.user_id = `user`.id WHERE user_group.source_id = #{member.workspaceId}
AND `user`.name like CONCAT('%', #{member.name},'%')
order by user_group.update_time desc) temp

select distinct user.id, user.name, user.email, user.phone, user.create_time from user join user_group ug on user.id = ug.user_id where ug.group_id = #{request.userGroupId}
and user.name like concat('%', #{request.name},'%')
order by ug.update_time desc

SELECT COUNT(id)
FROM user_group ug
WHERE ug.user_id = #{userId}
AND ug.source_id = #{sourceId}
AND ug.group_id = #{groupId}
