mapper.select:
SELECT
t.id,
t.project_id,
t.oper_user,
t.oper_type,
UPPER (t.oper_module) oper_module ,
t.oper_title,
t.oper_time,
t2.`name` projectName ,
t2.workspace_id workspace_id
FROM
operating_log t
LEFT JOIN project t2 ON t.project_id = t2.id


        t.project_id IN

            #{projectId}



        AND t.oper_user LIKE #{request.operUser, jdbcType=VARCHAR}


        AND t.project_id = #{request.projectId}


        AND t2.workspace_id = #{request.workspaceId}


        AND t.oper_type LIKE #{request.operType, jdbcType=VARCHAR}


        AND t.oper_module IN

            #{item}



        AND t.oper_module IN

            #{item}



        AND t.oper_time BETWEEN #{request.startTime} AND #{request.endTime}



ORDER BY t.oper_time DESC

SELECT tt.*, t1.NAME userName,t2.`name` projectName FROM ( SELECT t.id, t.project_id, t.oper_user, t.source_id, t.oper_type, t.oper_module, t.oper_title, t.oper_time, t.oper_content FROM operating_log t INNER JOIN operating_log_resource t3 ON t.id = t3.operating_log_id
t.oper_type IN ('UPDATE','BATCH_UPDATE') AND t.oper_content IS NOT NULL
AND t3.source_id = #{request.sourceId, jdbcType=VARCHAR}
AND JSON_UNQUOTE(JSON_EXTRACT(t.oper_content, '$.columns[*]')) IS NOT NULL
)tt LEFT JOIN `user` t1 ON tt.oper_user = t1.id LEFT JOIN project t2 ON tt.project_id = t2.id ORDER BY tt.oper_time DESC
