mapper.select:
select schedule.* from schedule


        and schedule.group = #{request.group}


        and schedule.workspace_id = #{request.workspaceId}


        and schedule.project_id = #{request.projectId}


        and schedule.enable = #{request.enable}






                        and schedule.group in

                            #{value}








    order by

        ${order.name} ${order.type}

SELECT COUNT(id) AS countNumber
FROM `schedule`
WHERE resource_id IN (
    SELECT scene.id
    FROM api_scenario scene
    WHERE scene.project_id = #{projectId,jdbcType=VARCHAR}
      AND scene.status != 'Trash' AND latest = 1
)
  AND create_time BETWEEN #{startTime} AND #{endTime}

SELECT sch.id AS taskID, sch.`name` AS `name`, sch.`value` AS rule, sch.`enable` AS `taskStatus`, sch.update_time AS updateTime, sch.id AS taskID, sch.`value` AS rule, sch.`enable` AS `taskStatus`, u.`name` AS creator, sch.update_time AS updateTime, sch.type AS taskType, sch.`group` AS taskGroup, sch.resource_id AS scenarioId FROM ( schedule sch left join user u ON sch.user_id = u.id ) WHERE sch.`enable` = true AND sch.project_id = #{projectId,jdbcType=VARCHAR}
and sch.group in
#{value}
