mapper.select:
select field_id
from custom_field_template
where custom_field_template.template_id = #{templateId}

select field_id, template_id, required, default_value, custom_data, cft.id as id, cf.name as name, cf.type as type, cf.remark as remark, cf.`system` as `system`, cf.options as options from custom_field_template cft inner join custom_field cf on cft.field_id = cf.id
where cft.template_id = #{request.templateId}
order by `order` asc

select id, field_id fieldId, template_id templateId, scene, required, `order`, default_value defaultValue, custom_data customData, `key`
from custom_field_template
where custom_field_template.template_id = #{templateId}

select `order` from custom_field_template where template_id = #{templateId}

    and `order` > #{baseOrder}

order by `order` desc limit 1;
