mapper.select:
SELECT count(DISTINCT ref_id) AS countNumber
FROM test_case
WHERE test_case.project_id = #{projectId}
  and (test_case.status != 'Trash' or test_case.status is null)
  and latest = 1
  AND create_time BETWEEN #{firstDayTimestamp}
    AND #{lastDayTimestamp}

SELECT count(distinct test_case.ref_id) AS countNumber
FROM test_case
         join test_case_test on test_case.id = test_case_test.test_case_id
WHERE test_case.project_id = #{projectId}
  and (test_case.status != 'Trash' or test_case.status is null)
  and latest = 1
  AND test_case_test.create_time BETWEEN #{firstDayTimestamp}
    AND #{lastDayTimestamp}

select count(test_case.id) from test_case where test_case.project_id = #{projectId} and (test_case.status != 'Trash' or test_case.status is null) and latest = 1 and test_case.id in ( SELECT test_case_id FROM test_case_test WHERE test_type = 'testCase' and test_id IN (select id FROM api_test_case WHERE `STATUS` is null or status != 'Trash') UNION SELECT test_case_id FROM test_case_test WHERE test_type = 'performance' and test_id IN (select id from load_test) UNION SELECT test_case_id FROM test_case_test WHERE test_type = 'automation' and test_id IN (select id FROM api_scenario WHERE `STATUS` != 'Trash')
UNION SELECT test_case_id FROM test_case_test WHERE test_type = 'uiAutomation' and test_id IN (select id FROM ui_scenario WHERE `STATUS` != 'Trash')
)

