mapper.select:
select id,status from api_scenario_report
WHERE id IN

    #{value}

SELECT * FROM ( SELECT scene.id AS testCaseID, scene.id AS id, scene.`name` AS caseName, apiScene.testPlanName AS testPlan, apiScene.testPlanId AS testPlanId, count(executionInfo.id) AS failureTimes, 'scenario' AS caseType FROM scenario_execution_info executionInfo INNER JOIN ( SELECT testPlanScenario.id, testPlanScenario.api_scenario_id, testPlan.id AS testPlanId, testPlan.`name` AS testPlanName FROM test_plan_api_scenario testPlanScenario INNER JOIN test_plan testPlan ON testPlan.id = testPlanScenario.test_plan_id WHERE testPlan.project_id = #{projectId} ) apiScene ON apiScene.id = executionInfo.source_id INNER JOIN api_scenario scene ON scene.id = apiScene.api_scenario_id WHERE scene.`status` != 'Trash' AND ( executionInfo.result = 'ERROR' ) AND executionInfo.create_time >= #{startTimestamp}
AND executionInfo.version = #{versionId}
GROUP BY scene.id,apiScene.testPlanId ) showTable ORDER BY showTable.failureTimes DESC LIMIT ${limitNumber}
