mapper.select:
select test_plan_test_case.id as id, test_case.id as caseId, test_case.name, test_case.priority, test_case.type, test_case.test_id as testId,test_case.node_id, test_case.tags, test_case.maintainer, test_case.custom_fields, test_case.node_path, test_case.method, test_case.num, test_case.custom_num, test_case.project_id, test_plan_test_case.executor, test_plan_test_case.status as status, test_case.status as caseStatus, test_plan_test_case.actual_result, test_plan_test_case.update_time, test_plan_test_case.create_time, test_plan_test_case.issues_count as issuesCount, test_plan_test_case.plan_id as planId, test_case.version_id as versionId, test_case.review_status from test_plan_test_case inner join test_case on test_plan_test_case.case_id = test_case.id
order by
test_case.${order.name} ${order.type}
test_plan_test_case.${order.name} ${order.type}

select test_plan_test_case.*, test_case.*
from test_plan_test_case
inner join test_case on test_plan_test_case.case_id = test_case.id


        and test_plan_test_case.plan_id = #{request.planId}


        and test_case.method = #{request.method}



        and test_case.node_path in

            #{nodePaths}

select test_plan_test_case.*, test_case.* from test_plan_test_case inner join test_case on test_plan_test_case.case_id = test_case.id
and test_plan_test_case.plan_id = #{request.planId}
and test_case.method = #{request.method}
and
test_case.node_path like concat('%',#{nodePaths},'%')

select distinct plan_id from test_plan_test_case
inner join test_plan
on test_plan_test_case.plan_id = test_plan.id

    test_plan_test_case.executor = #{userId}
    and test_plan.workspace_id = #{workspaceId}

        and test_plan.project_id = #{projectId}

select test_plan_test_case.*, test_case.* from test_plan_test_case inner join test_case on test_plan_test_case.case_id = test_case.id where test_plan_test_case.status != 'Prepare' and test_plan_test_case.status != 'Underway' and test_plan_test_case.Executor = #{request.executor} and test_plan_test_case.plan_id in
#{planId}
order by test_plan_test_case.update_time desc

select test_plan_test_case.*, test_case.* from test_plan_test_case inner join test_case on test_plan_test_case.case_id = test_case.id where (status = 'Prepare' or status = 'Underway') and test_plan_test_case.Executor = #{request.executor} and test_plan_test_case.plan_id in
#{planId}
order by test_plan_test_case.update_time asc

select status
from test_plan_test_case
where plan_id = #{planId}

SELECT test_plan_api_case.id as reportId,test_plan_api_case.api_case_id as id,"definition" as type,api_test_case.name,test_plan_api_case.status from test_plan_api_case left join api_test_case on test_plan_api_case.api_case_id=api_test_case.id inner join api_definition a on api_test_case.api_definition_id = a.id and (a.status != 'Trash' or a.status is null)
and test_plan_api_case.test_plan_id = #{request.planId}
UNION ALL SELECT test_plan_api_scenario.api_scenario_id as testId,test_plan_api_scenario.id as id,"scenario" as type,api_scenario.name,test_plan_api_scenario.status from test_plan_api_scenario left join api_scenario on test_plan_api_scenario.api_scenario_id=api_scenario.id and (api_scenario.status != 'Trash' or api_scenario.status is null)
and test_plan_api_scenario.test_plan_id = #{request.planId}
UNION ALL select test_plan_load_case.id as reportId,load_test.id as id,"perform" as type,load_test.name as name,test_plan_load_case.status from test_plan_load_case inner join load_test on test_plan_load_case.load_case_id =load_test.id
and test_plan_load_case.test_plan_id = #{request.planId}
UNION ALL SELECT test_case.id as reportId,test_case.test_id as id,test_case.type as type,test_case.name,test_plan_test_case.status from test_plan_test_case left join test_case on test_plan_test_case.case_id =test_case.id
and test_plan_test_case.plan_id = #{request.planId}
and test_case.method = #{request.method}

select *
from test_case_test
where test_case_id = #{caseId};

select tptc.id, tc.id as caseId, tc.name, tc.priority, tc.num, tc.custom_num, tc.project_id, tc.node_id, tc.tags, tptc.actual_result, tptc.update_time, tptc.create_time, tptc.issues_count, tptc.status, tc.maintainer, tptc.plan_id, tptc.executor from test_plan_test_case tptc inner join test_case tc on tptc.case_id = tc.id where tptc.plan_id = #{planId}
and tptc.status in
#{status}
and tptc.is_del = 0 order by tptc.`order` desc

